import React from 'react'
import Particles from "react-tsparticles";
import { loadFull } from "tsparticles";
import "./ParticleBg.css"

const ParticleBg = () => {
    const particlesInit = async (main) => {
        console.log(main);
        await loadFull(main);
      };

      


  return (
    
    <Particles
  id="tsparticles"
  init={particlesInit}
  options={{
    fullScreen: {
      enable: true,
      zIndex: -1,
    },
    particles: {
      color: {
        value: '#ffffff', // Set the particle color to white
      },
      links: {
        color: '#ffffff', // Set the link color to white
        distance: 200,
        enable: true,
        opacity: 0.5,
        width: 1,
      },
      move: {
        enable: true,
        speed: 4, // Increase the speed of particle movement
        attract: {
          enable: true,
          rotateX: 600,
          rotateY: 1200,
        },
        trail: {
          enable: true,
          length: 5,
          fillColor: '#ffffff',
        },
      },
      number: {
        density: {
          enable: true,
          value_area: 800,
        },
        value: 50,
      },
      opacity: {
        value: 0.8,
      },
      shape: {
        type: 'circle',
      },
      size: {
        value: 4,
      },
    },
    background: {
      color: '#000000', // Set the background color to black
      image: '',
      position: '50% 50%',
      repeat: 'no-repeat',
      size: 'cover',
    },
    interactivity: {
      events: {
        onhover: {
          enable: true,
          mode: 'grab',
        },
      },
    },
    retina_detect: true,
    parallax: {
      enable: true,
      force: 2,
    },
  }}
/>

  )
}

export default ParticleBg
