import React from 'react'
import Particles from "react-tsparticles";
import { loadFull } from "tsparticles";
import "./ParticleBg.css"

const ParticleBg = () => {
    const particlesInit = async (main) => {
        console.log(main);
        await loadFull(main);
      };

      


  return (
    
    <Particles
      id="tsparticles"
      init={particlesInit}
      options={{
        fullScreen: {
          enable: true,
          zIndex: -1,
        },
        particles: {
          preset: 'links', // Use the "links" preset
          color: {
            value: '#ffffff', // Set the particle color to white
          },
        },
        background: {
          color: '#000000', // Set the background color to black
          image: '',
          position: '50% 50%',
          repeat: 'no-repeat',
          size: 'cover',
        },
        interactivity: {
          events: {
            onhover: {
              enable: true,
              mode: ['grab'],
            },
            onclick: {
              enable: false,
              mode: 'bubble',
            },
            resize: true,
          },
          modes: {
            grab: {
              distance: 400,
              line_linked: {
                opacity: 1,
              },
            },
            bubble: {
              distance: 400,
              size: 40,
              duration: 2,
              opacity: 8,
              speed: 3,
            },
            repulse: {
              distance: 200,
            },
            push: {
              particles_nb: 4,
            },
            remove: {
              particles_nb: 2,
            },
          },
        },
        retina_detect: true,
        parallax: {
          enable: true,
          force: 2,
        },
      }}
    />
  )
}

export default ParticleBg
