import React from 'react'
import Particles from "react-tsparticles";
import { loadFull } from "tsparticles";
import "./ParticleBg.css"

const ParticleBg = () => {
    const particlesInit = async (main) => {
        console.log(main);
        await loadFull(main);
      };

      


  return (
    
    <Particles
      id="tsparticles"
      init={particlesInit}
      options={{
        fullScreen: {
          enable: true,
          zIndex: -1,
        },
        particles: {
          number: {
            value: 50,
          },
          color: {
            value: '#ffffff', // Set the particle color to white
          },
          shape: {
            type: 'circle',
          },
          opacity: {
            value: 0.8,
          },
          size: {
            value: 4,
            random: true,
            anim: {
              enable: false,
              speed: 40,
              size_min: 0.1,
              sync: false,
            },
          },
          move: {
            enable: true,
            speed: 2,
          },
        },
        background: {
          color: '#000000', // Set the background color to black
          image: '',
          position: '50% 50%',
          repeat: 'no-repeat',
          size: 'cover',
        },
        interactivity: {
          events: {
            onhover: {
              enable: true,
              mode: 'repulse',
            },
          },
        },
        retina_detect: true,
        parallax: {
          enable: true,
          force: 2,
        },
      }}
    />
  )
}

export default ParticleBg
