import React from 'react'
import Particles from "react-tsparticles";
import { loadFull } from "tsparticles";
import "./ParticleBg.css"

const ParticleBg = () => {
    const particlesInit = async (main) => {
        console.log(main);
        await loadFull(main);
      };

      


  return (
    
    <Particles
      id="tsparticles"
      init={particlesInit}
      options={{
        fullScreen: {
          enable: true,
          zIndex: -1,
        },
        particles: {
          color: {
            value: '#ffffff',
          },
          links: {
            color: '#ffffff',
            distance: 200,
            enable: true,
            opacity: 0.5,
            width: 1,
          },
          move: {
            enable: true,
            speed: 5, // Increase the speed for faster movement
            attract: {
              enable: true,
              rotateX: 600,
              rotateY: 1200,
            },
          },
          number: {
            density: {
              enable: true,
              value_area: 800,
            },
            value: 50,
          },
          opacity: {
            value: 0.8,
          },
          shape: {
            type: 'circle', // Change the shape to circle
          },
          size: {
            value: 4,
          },
        },
        background: {
          color: '#000000',
          image: '',
          position: '50% 50%',
          repeat: 'no-repeat',
          size: 'cover',
        },
        interactivity: {
          events: {
            onhover: {
              enable: true,
              mode: 'grab',
            },
          },
          modes: {
            grab: {
              distance: 150,
              line_linked: {
                opacity: 1,
              },
            },
            attract: {
              distance: 200,
              duration: 0.4,
              speed: 1,
            },
          },
        },
        retina_detect: true,
        parallax: {
          enable: true,
          force: 2,
        },
      }}
    />
  )
}

export default ParticleBg
