import React from 'react'
import Particles from "react-tsparticles";
import { loadFull } from "tsparticles";
import "./ParticleBg.css"

const ParticleBg = () => {
    const particlesInit = async (main) => {
        console.log(main);
        await loadFull(main);
      };

      


  return (
    
    <Particles
  id="tsparticles"
  init={particlesInit}
  options={{
    fullScreen: {
      enable: true,
      zIndex: -1,
    },
    particles: {
      color: {
        value: '#ffffff', // Set the particle color to white
      },
      links: {
        color: '#ffffff', // Set the link color to white
        distance: 200,
        enable: true,
        opacity: 0.5,
        width: 1,
      },
      move: {
        enable: true,
        speed: 2,
        attract: {
          enable: true, // Enable attraction
          rotateX: 3000, // Attraction rotation X-axis
          rotateY: 3000, // Attraction rotation Y-axis
          speed: 8, // Adjust the attraction speed
        },
      },
      number: {
        density: {
          enable: true,
          value_area: 650,
        },
        value: 50,
      },
      opacity: {
        value: 0.8,
      },
      shape: {
        type: 'particle',
      },
      size: {
        value: 4,
      },
    },
    background: {
      color: '#000000', // Set the background color to black
      image: '',
      position: '50% 50%',
      repeat: 'no-repeat',
      size: 'cover',
    },
    interactivity: {
      events: {
        onhover: {
          enable: true,
          mode: 'attract', // Change mode to attract
        },
      },
      modes: {
        attract: {
          distance: 200, // Adjust the attraction distance
          line_linked: {
            opacity: 1,
          },
        },
      },
    },
    garb: {
      distance: 100,
      line_linked: { opacity: 1 },
    },
    retina_detect: true,
    parallax: {
      enable: true,
      force: 2,
    },
  }}
/>

  )
}

export default ParticleBg
